mapper.select:
        SELECT STUDENT_SEQ_ID , STUDENT_ID , STUDENT_NAME , DORMITORY_ID
--         SELECT COUNT(*)
        FROM STUDENT_INFO
        WHERE STUDENT_ID = #{studentId}
          AND STUDENT_PW = #{studentPw}

SELECT STUDENT_SALT
FROM STUDENT_INFO
WHERE STUDENT_ID = #{studentId}

mapper.insert:
UPDATE STUDENT_INFO
SET STUDENT_REFRESH_TOKEN = #{refreshToken}
WHERE STUDENT_ID = #{studentId}
